import React, { useReducer } from "react";

const initialState = {
  number: 0,
};

const reducer = (state, action) => {
  switch (action.type) {
    case "load":
      return {
        ...state,
        number: action.payload,
      };

    default:
      break;
  }
};

const Test = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div>
      <button
        onClick={() => dispatch({ type: "inc", payload: state.number++ })}
      >
        Incremenet
      </button>
      {state.number}
    </div>
  );
};

export default Test;
