import React, { useEffect, useState } from "react";
import "../components/Inbox.css";
import { useStateValue } from "../DataContext/DataContext";
import Button from "@material-ui/core/Button";
import Checkbox from "@material-ui/core/Checkbox";

const Inbox = () => {
  const [{ inbox }, dispatch] = useStateValue();

  const [checked, setChecked] = useState(false);

  const handleChange = (event) => {
    setChecked(event.target.checked);
  };

  const fetchInbox = async () => {
    const data = await fetch(
      "https://dummyapi.io/data/api/post?limit=100",
      // gets user posts
      // "https://dummyapi.io/data/api/user/0F8JIqi4zwvb77FGz6Wt/post?limit=100",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "app-id": "MJg47OQxEGTwunLzeonB",
        },
      }
    );
    const res = await data.json();
    dispatch({ type: "load_inbox", payload: res.data });
  };

  useEffect(() => {
    if (inbox.length === 0) fetchInbox();
  }, [inbox]);

  console.log(inbox);

  return (
    <div className="inbox__container">
      <ul>
        {inbox
          .map((item) => (
            <li className="inbox__item">
              <div className="checkbox__container">
                <Checkbox
                  color="primary"
                  inputProps={{ "aria-label": "secondary checkbox" }}
                />
              </div>
              <div className="add__favorites">
                <img
                  src="https://www.gstatic.com/images/icons/material/system/2x/star_border_white_20dp.png"
                  width="20"
                  height="20"
                />
              </div>
              <div className="sender__img">
                <img src={item.image} width="24" height="24" />
              </div>
              <div className="sender">
                {item.owner.firstName} {item.owner.lastName}
              </div>
              <div className="preview"> {item.text}</div>
              <div className="date"> {item.publishDate}</div>

              <Button variant="contained" color="primary">
                Hello World
              </Button>
            </li>
          ))
          .splice(0, 25)}
      </ul>
    </div>
  );
};

export default Inbox;
