import React, { useEffect, useState } from "react";
import "../components/Preview.css";
import { useStateValue } from "../DataContext/DataContext";
const Preview = () => {
  const [{ preview, inbox }, dispatch] = useStateValue();
  const [selectedEmail, setSelectedEmail] = useState([]);

  useEffect(() => {
    if (selectedEmail.length === 0) {
      setSelectedEmail(inbox.filter((item) => item.id === preview));
      inbox.filter((item) => item.id === preview).isRead = true;
    }
  }, [selectedEmail]);

  return (
    <div className="preview__container">
      <div className="preview">
        {selectedEmail.map((item) => (
          <li className="inbox__item">
            <div className="checkbox__container"></div>
            <div className="add__favorites">
              <img
                src="https://www.gstatic.com/images/icons/material/system/2x/star_border_white_20dp.png"
                width="20"
                height="20"
              />
            </div>
            <div className="sender__img">
              <img src={item.image} width="24" height="24" />
            </div>
            <div className="sender">
              {item.owner.firstName} {item.owner.lastName}
            </div>
            <div className="preview"> {item.text}</div>
            <div className="date"> {item.publishDate}</div>
          </li>
        ))}
      </div>
    </div>
  );
};

export default Preview;
