import React, { useEffect, useState } from "react";

const useFetch = ({ content, url }) => {
  useEffect(() => {
    (async function () {
      const data = await fetch(url, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "app-id": "MJg47OQxEGTwunLzeonB",
        },
      });
      const res = await data.json();
      res = content;
    });
  }, [content]);

  return { content };
};

export default useFetch;
