import React, { useEffect, useState } from "react";
import { useStateValue } from "../DataContext/DataContext";
import useFetch from "../hooks/useFetch";

const Pagination = () => {
  const [{ inbox, page }, dispatch] = useStateValue();
  const [pageVal, setPageVal] = useState(25);

  const res = useFetch(`https://dummyapi.io/data/api/post?limit=${page + 25}`);
  const [activeEmail, setActiveEmail] = useState("UWdcOFTc7DfzOhI6LpI4");

  return (
    <div className="pagination__container">
      <div>{`0-${pageVal}`}</div>
      <button
        onClick={() => {
          dispatch({ type: "load_inbox", payload: res.response.data });
        }}
      >
        {">"}{" "}
      </button>
    </div>
  );
};

export default Pagination;
