import React, { useState } from "react";
import "../components/Compose.css";

const Compose = () => {
  const [popupVisible, setPopupVisible] = useState(false);

  const ComposePopup = () => {
    return (
      <div className="compose__window">
        <h1>New Message</h1>
        <div>
          <a> - </a>
          <a> / </a>
          <a> x </a>
        </div>

        <div className="compose__header">
          <input className="recipients"></input>
          <input className="subject"></input>
        </div>
        <div className="compose__body"></div>
        <div>
          <button>Send</button>
          <button>Delete</button>
        </div>
      </div>
    );
  };

  return (
    <div
      className="compose__container"
      onClick={() => {
        if (!popupVisible) {
          setPopupVisible(true);
        } else {
          setPopupVisible(false);
        }
      }}
    >
      <div class="compose" role="button" tabindex="0">
        Compose
      </div>
      {popupVisible ? <ComposePopup /> : null}
    </div>
  );
};

export default Compose;
