import React, { useEffect, useRef, useState } from "react";
import "../components/Inbox.css";
import { useStateValue } from "../DataContext/DataContext";
import Button from "@material-ui/core/Button";
import Checkbox from "@material-ui/core/Checkbox";
import { useHistory } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import Actions from "../Actions";

const Inbox = ({ actionsVisible }) => {
  const [{ inbox, page, nav }, dispatch] = useStateValue();
  const history = useHistory(null);
  const [visible, setVisible] = useState(false);
  // const [page, setPage] = useState(25);
  const res = useFetch(`https://dummyapi.io/data/api/post?limit=${page}`);
  const [activeEmail, setActiveEmail] = useState("UWdcOFTc7DfzOhI6LpI4");

  const checkBoxRef = useRef(nav.map((item) => React.createRef()));

  useEffect(() => {
    if (res.response !== null && inbox.length !== page) {
      dispatch({ type: "load_inbox", payload: res.response.data, page: page });
    }
  }, [res.response, page]);

  return (
    <div className="content__container">
      <div className="pagination__container">
        <Actions actionsVisible={visible} />
        <div className="pagination">
          {" "}
          {page > 25 ? (
            <button
              onClick={() => {
                console.log(page - 25);
                dispatch({
                  type: "load_inbox",
                  payload: res.response.data,
                  page: page - 25,
                });
              }}
            >
              {"<"}
            </button>
          ) : null}
          {page - 25}-{page}
          <button
            onClick={() => {
              console.log(page + 25);
              dispatch({
                type: "load_inbox",
                payload: res.response.data,
                page: page + 25,
              });
            }}
          >
            {">"}
          </button>
        </div>
      </div>
      <div className="inbox__container">
        <ul>
          {inbox
            .map((item, index) => (
              <li
                id={item.id}
                className={`inbox__item ${
                  item.isRead == false ? "selected__email" : ""
                }`}
              >
                <div className="checkbox__container">
                  <Checkbox
                    ref={checkBoxRef.current[index]}
                    id={item.id}
                    onClick={(e) => {
                      if (
                        document.querySelectorAll("input:checked").length > 0
                      ) {
                        setVisible(true);
                      } else {
                        setVisible(false);
                      }
                      console.log(checkBoxRef.querySelectorAll("checked"));
                      // tabRefs.current[currentFocus].current
                    }}
                    color="primary"
                    inputProps={{ "aria-label": "secondary checkbox" }}
                    // checked={item.id === za ? true : false}
                  />
                </div>
                <div className="add__favorites">
                  <img
                    src="https://www.gstatic.com/images/icons/material/system/2x/star_border_white_20dp.png"
                    width="20"
                    height="20"
                  />
                </div>
                <div
                  style={{
                    display: "flex",
                    width: "100%",
                    alignItems: "center",
                  }}
                  onClick={(e) => {
                    console.log(activeEmail);
                    dispatch({ type: "preview_email", payload: item.id });
                    setActiveEmail(item.id);
                    history.push("/preview");
                  }}
                >
                  {" "}
                  <div className="sender__img">
                    <img src={item.image} width="24" height="24" />
                  </div>
                  <div className="sender">
                    {item.owner.firstName} {item.owner.lastName}
                  </div>
                  <div className="preview"> {item.text}</div>
                  <div className="date"> {item.publishDate}</div>
                </div>
              </li>
            ))
            .splice(page - 25, page)}
        </ul>
      </div>
    </div>
  );
};

export default Inbox;
