import React, { useEffect, useState } from "react";

const useFetch = ({ content, url }) => {
  async function za() {
    const data = await fetch("https://dummyapi.io/data/api/post?limit=100", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "app-id": "MJg47OQxEGTwunLzeonB",
      },
    });
    const res = await data.json();
    content = res;
  }
  useEffect(() => {
    za();
    console.log(content);
  }, [content]);

  return { content };
};

export default useFetch;
