import React, { useEffect, useState } from "react";
import { useStateValue } from "../DataContext/DataContext";
import useFetch from "../hooks/useFetch";

const Pagination = () => {
  const [{ inbox, page }, dispatch] = useStateValue();

  const res = useFetch(`https://dummyapi.io/data/api/post?limit=${page}`);

  useEffect(() => {
    if (res.response !== null) {
      dispatch({
        type: "next_page",
        page: page + 25,
        inbox: res.response.data,
      });
    }
  }, [page]);

  return (
    <div className="pagination__container">
      <div>{`${page - 25}-${page}`}</div>
      <button
        onClick={() => {
          res();
        }}
      >
        {">"}{" "}
      </button>
    </div>
  );
};

export default Pagination;
